import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Pagination } from 'react-bootstrap';

function SearchFriends() {
  const [searchQuery, setSearchQuery] = useState('');
  const [users, setUsers] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  const currentUser = sessionStorage.getItem('username');

  const handleSearch = async (page) => {
    try {
      const response = await axios.get(`http://localhost:8080/users/usersList/search?query=${searchQuery}&currentUser=${currentUser}&page=${page}`);
      setUsers(response.data.content);
      setTotalPages(response.data.totalPages);
      setErrorMessage('');
    } catch (error) {
      setUsers([]);
      if (error.response) {
        const errorMessage = error.response.data.message;
        setErrorMessage(errorMessage);
      } else {
        setErrorMessage('An error occurred while searching for users.');
      }
    }
  };

  useEffect(() => {
    handleSearch(currentPage - 1);
  }, [currentPage]);

  const renderAvatar = (user) => {
    if (user.avatarUrl) {
      return <img src={user.avatarUrl} alt={`${user.username}'s avatar`} />;
    } else {
      const initials = user.username.charAt(0).toUpperCase();
      return (
        <div className="initials-circle">
          <p>{initials}</p>
        </div>
      );
    }
  };

  const renderPagination = () => {
    const items = [];
    for (let number = 1; number <= totalPages; number++) {
      items.push(
        <Pagination.Item key={number} active={number === currentPage} onClick={() => setCurrentPage(number)}>
          {number}
        </Pagination.Item>
      );
    }
    return (
      <div className="pagination-wrapper">
        <Pagination>{items}</Pagination>
      </div>
    );
  };

  return (
    <div>
      <h1>Search for Friends</h1>
      <div>
        <label htmlFor="search-input">Search: </label>
        <input id="search-input" type="text" value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} />
        <button onClick={() => handleSearch(0)}>Search</button>
      </div>
      {errorMessage && <p>{errorMessage}</p>}
      <div className="user-list">
        {users.map((user) => (
          <div key={user.id} className="user-item">
            <div className="avatar-wrapper">{renderAvatar(user)}</div>
            <div className="user-info">
              <p>{user.username}</p>
              <p>{user.email}</p>
            </div>
          </div>
        ))}
      </div>
      {totalPages > 1 && renderPagination()}
    </div>
  );
}

export default SearchFriends;
