import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function SearchFriends() {
    const [searchText, setSearchText] = useState("");
  const [users, setUsers] = useState([]);
  const [errorMessage, setErrorMessage] = useState("");

  useEffect(() => {
    axios
      .get("http://localhost:8080/users")
      .then((response) => {
        setUsers(response.data.filter((user) => user.username !== sessionStorage.getItem('username')));
      })
      .catch((error) => {
        setErrorMessage(error.message);
      });
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    axios
      .get(`http://localhost:8080/users/search?searchText=${searchText}`)
      .then((response) => {
        setUsers(response.data.filter((user) => user.username !== sessionStorage.getItem('username')));
      })
      .catch((error) => {
        setErrorMessage(error.message);
      });
  };
    

  return (
    <div className='container'>
        <div className='py-4'>
        <div>
      <h2>Users</h2>
      <input type="text" placeholder="Search" value={searchTerm} onChange={e => setSearchTerm(e.target.value)} />
      <div className="user-list">
        {users.map(user => (
          <div key={user.id} className="user-card">
            <div className="user-avatar">
              <img src={`http://localhost:8080/${user.avatarPath}`} alt={`${user.username} avatar`} />
            </div>
            <div className="user-details">
              <div className="user-name">{user.username}</div>
              <div className="user-email">{user.email}</div>
            </div>
          </div>
        ))}
      </div>
    </div>
        </div>
    </div>
  )
}
