import axios from "axios";
import React, { useState } from "react";

export default function AddJob() {
  const [job, setJob] = useState({
    name: "",
    image: "",
    role: "",
  });

  const onInputChange = (e) => {
    setJob({ ...job, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:8080/jobs/createJob", job);
      console.log("Job created successfully!");
      // Optionally redirect to a success page or perform any other action
    } catch (error) {
      console.error("An error occurred while creating the job.", error);
    }
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-md-6 offset-md-3 border rounded p-4 mt-2 shadow">
          <h2 className="text-center m-4">Create Job</h2>
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="name" className="form-label">
                Name
              </label>
              <input
                type="text"
                className="form-control"
                placeholder="Name"
                name="name"
                value={job.name}
                onChange={onInputChange}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="image" className="form-label">
                Image
              </label>
              <input
                type="text"
                className="form-control"
                placeholder="Image"
                name="image"
                value={job.image}
                onChange={onInputChange}
              />
            </div>
            <div className="mb-3">
              <label htmlFor="role" className="form-label">
                Role
              </label>
              <input
                type="text"
                className="form-control"
                placeholder="Role"
                name="role"
                value={job.role}
                onChange={onInputChange}
              />
            </div>
            <button type="submit" className="btn btn-primary">
              Create Job
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}
