import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, Card, Container, Row, Col } from 'react-bootstrap';
import { Link } from 'react-router-dom';

export default function LogenHome() {
  if (!sessionStorage.getItem('username')) {
    window.location.href = '/';
  }
  const [pendingFriends, setPendingFriends] = useState([]);
  const [confirmedFriends, setConfirmedFriends] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');
  const currentUser = sessionStorage.getItem('username');

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get(`http://localhost:8080/friend/pending?username=${currentUser}`);
        setPendingFriends(response.data);
        setErrorMessage('');
      } catch (error) {
        setPendingFriends([]);
        if (error.response) {
          const errorMessage = error.response.data.message;
          setErrorMessage(errorMessage);
        } else {
          setErrorMessage('An error occurred while fetching pending friends.');
        }
      }
    }
    fetchData();
  }, [currentUser]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get(`http://localhost:8080/friend/confirmed?username=${currentUser}`);
        setConfirmedFriends(response.data);
        setErrorMessage('');
      } catch (error) {
        setConfirmedFriends([]);
        if (error.response) {
          const errorMessage = error.response.data.message;
          setErrorMessage(errorMessage);
        } else {
          setErrorMessage('An error occurred while fetching confirmed friends.');
        }
      }
    }
    fetchData();
  }, [currentUser]);

  function handleAddFriend() {}

  function handleCancelFriendship(friendId) {
    // Implement your cancel friendship logic here
  }

  return (
    <Container fluid style={{ marginTop: '20px' }}>
      <Row>
        <Col xs={3} style={{borderLeft:"#0d6efd 3px solid",borderRight:"#0d6efd 3px solid", overflow: 'hidden'}}>
          <h2>Friend List</h2>
          <div className="friend-list">
          {confirmedFriends.map(friend => (
            friend.pending === false && friend.sender.username === currentUser ? (
              <Card key={friend.id} className="friend-item" border="primary">
                  <a href='#' style={{ textDecoration: "none" }}>
                    <div className="friend-info" style={{ display: 'flex' }}>
                    <div className="friend-avatar">
                    <img
                      src={
                        friend.receiver.avatarPath ||
                        `https://ui-avatars.com/api/?name=${friend.receiver.username}&background=007bff&color=fff`
                      }
                      style={friend.sender.avatarPath ? { width: '100%', height: 'auto' } : {}}
                      alt="Friend Avatar"
                      className="avatar"
                    />
                </div>
                      <div className="friend-details" style={{ width: "100%" }}>
                        <p className="username" style={{ fontSize: "20px", fontWeight: "bold", position: "relative", top: "15px", height: "100%", textAlign: "center", width: "100%" }}>{friend.receiver.username}</p>
                      </div>
                    </div>
                  </a>
                </Card>
                ) : friend.pending === false && friend.sender.username === currentUser ? (
                  <Card key={friend.id} className="friend-item" border="primary">
                      <a href='#' style={{ textDecoration: "none" }}>
                        <div className="friend-info" style={{ display: 'flex' }}>
                        <div className="friend-avatar">
                            <img
                              src={
                                friend.sender.avatarPath ||
                                `https://ui-avatars.com/api/?name=${friend.sender.username}&background=007bff&color=fff`
                              }
                              style={friend.sender.avatarPath ? { width: '100%', height: 'auto' } : {}}
                              alt="Friend Avatar"
                              className="avatar"
                            />
                        </div>
                          <div className="friend-details" style={{ width: "100%" }}>
                            <p className="username" style={{ fontSize: "20px", fontWeight: "bold", position: "relative", top: "15px", height: "100%", textAlign: "center", width: "100%" }}>{friend.sender.username}</p>
                          </div>
                        </div>
                      </a>
                    </Card>
                ) : null
              ))}
          </div>
        </Col>
        <Col xs={6}>
          <div></div>
        </Col>
        <Col xs={3} style={{borderLeft:"#0d6efd 3px solid",borderRight:"#0d6efd 3px solid", overflow: 'hidden'}}>
          <h2>Friend Request</h2>
          <div className="friend-list">
            {pendingFriends.map(friend => (
              <Card key={friend.sender.id} className="friend-item" border="primary">
                <div className="friend-info" style={{ display: 'flex' }}>
                  <div className="friend-avatar">
                    <img
                      src={
                        friend.sender.avatarPath ||
                        `https://ui-avatars.com/api/?name=${friend.sender.username}&background=007bff&color=fff`
                      }
                      style={friend.sender.avatarPath ? { width: '100%', height: 'auto' } : {}}
                      alt="Friend Avatar"
                      className="avatar"
                    />
                </div>
                  <p className="username" style={{fontWeight:"bold",position:"relative",top:"15px",height:"100%", textAlign:"center",width:"100%"}}>{friend.sender.username}</p>
                  <div className="friend-details" style={{ width:"100%", display: 'flex', justifyContent: 'flex-end' }}>
                    
                    <Button variant="primary" onClick={() => handleAddFriend(friend.id)}>
                      Add Friend
                    </Button>
                    <Button variant="danger" onClick={() => handleCancelFriendship(friend.id)}>
                      Cancel Friendship
                    </Button>
                  </div>
                </div>
              </Card>
            ))}
          </div>
        </Col>
      </Row>
    </Container>
  );
}
