import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Row, Col } from 'react-bootstrap';
import { Link } from 'react-router-dom';

export default function LogenHome() {
    if(!sessionStorage.getItem("username")){
        window.location.href = '/';
    }
    const [pendingFriends, setPendingFriends] = useState([]);
  const [confirmedFriends, setConfirmedFriends] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');
  const currentUser = sessionStorage.getItem('username');

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get(`http://localhost:8080/friend/pending?username=${currentUser}`);
        setPendingFriends(response.data);
        setErrorMessage('');
      } catch (error) {
        setPendingFriends([]);
        if (error.response) {
          const errorMessage = error.response.data.message;
          setErrorMessage(errorMessage);
        } else {
          setErrorMessage('An error occurred while fetching pending friends.');
        }
      }
    }
    fetchData();
  }, [currentUser]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get(`http://localhost:8080/friend/confirmed?username=${currentUser}`);
        setConfirmedFriends(response.data);
        setErrorMessage('');
      } catch (error) {
        setConfirmedFriends([]);
        if (error.response) {
          const errorMessage = error.response.data.message;
          setErrorMessage(errorMessage);
        } else {
          setErrorMessage('An error occurred while fetching confirmed friends.');
        }
      }
    }
    fetchData();
  }, [currentUser]);


  return (
        <Container fluid style={{ marginTop: '20px' }}>
        <Row>
            
        </Row>
        <Row>
            <Col xs={3}>
            <h2>Friend List</h2>
            </Col>
            <Col xs={6}>
            <h2>Chat</h2>
            </Col>
            <Col xs={3}>
            <h2>Friend Request</h2>
            </Col>
        </Row>
        <Row
            style={{
            height: "60vh",
            backgroundColor: "#f2f2f2",
            padding: "20px",
            borderRadius: "10px",
            margin: "20px 0",
            }}
        >
            <Col
            xs={3}
            style={{
                borderRight: "1px solid #ddd",
                height: "100%",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
            }}
            >
            <ul>
                {confirmedFriends.map(friend => (
                <li key={friend.id}>{friend.sender.name}</li>
                ))}
            </ul>
            </Col>
            <Col
            xs={6}
            style={{
                borderRight: "1px solid #ddd",
                borderLeft: "1px solid #ddd",
                height: "100%",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
            }}
            >
            <h2>Title 2</h2>
            <p>Content for column 2</p>
            </Col>
            <Col
            xs={3}
            style={{
                borderLeft: "1px solid #ddd",
                height: "100%",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
            }}
            >
            <ul>
                {pendingFriends.map(friend => (
                <li key={friend.id}>{friend.sender.avatarPath}{friend.sender.username}</li>
                ))}
            </ul>
            </Col>
        </Row>
        </Container>
  )
}
