import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

export default function RaidList() {
  const [raids, setRaids] = useState([]);

  useEffect(() => {
    fetchRaids();
  }, []);

  const fetchRaids = async () => {
    try {
      const response = await axios.get("http://localhost:8080/raids/getRaids");
      setRaids(response.data);
    } catch (error) {
      console.error("Failed to fetch raids", error);
    }
  };

  const handleEditRaid = (raidId) => {
    // Redirect to the edit raid page passing the raid ID
    // You can implement the routing mechanism according to your setup
    // For example, using react-router-dom's useHistory hook
    // history.push(`/raids/${raidId}/edit`);
    console.log(`Edit Raid ID: ${raidId}`);
  };

  return (
    <div className="container">
      <h2>Raid List</h2>
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Level</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {raids.map((raid) => (
            <tr key={raid.id}>
              <td>{raid.image}</td>
              <td>{raid.name}</td>
              <td>{raid.lvl}</td>
              <td>
                <button
                  className="btn btn-primary"
                  onClick={() => handleEditRaid(raid.id)}
                >
                  Edit
                </button>
                <Link to={`/raids/${raid.id}/edit`} className="btn btn-primary ml-2">
                  Edit
                </Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
