import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Image } from 'react-bootstrap';

function SearchFriends() {
  const [searchQuery, setSearchQuery] = useState('');
  const [users, setUsers] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');

  const currentUser = sessionStorage.getItem('username');

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/users/usersList?currentUser=${currentUser}`);
        setUsers(response.data);
        setErrorMessage('');
      } catch (error) {
        setUsers([]);
        if (error.response) {
          const errorMessage = error.response.data.message;
          setErrorMessage(errorMessage);
        } else {
          setErrorMessage('An error occurred while fetching users.');
        }
      }
    };

    fetchUsers();
  }, [currentUser]);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/users/usersList/search?query=${searchQuery}&currentUser=${currentUser}`);
      setUsers(response.data);
      setErrorMessage('');
    } catch (error) {
      setUsers([]);
      if (error.response) {
        const errorMessage = error.response.data.message;
        setErrorMessage(errorMessage);
      } else {
        setErrorMessage('An error occurred while searching for users.');
      }
    }
  };

  const handleAvatar = (user) => {
    if (user.avatarUrl) {
      return <Image src={user.avatarUrl} alt={`${user.username}'s avatar`} />;
    } else {
      const initials = user.username.charAt(0).toUpperCase();
      return (
        <div className="avatar-placeholder">
          <div className="avatar-placeholder-circle">{initials}</div>
        </div>
      );
    }
  };

  return (
    <div>
      <h1>Search for Friends</h1>
      <div>
        <label htmlFor="search-input">Search: </label>
        <input id="search-input" type="text" value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} />
        <button onClick={handleSearch}>Search</button>
      </div>
      {errorMessage && <p>{errorMessage}</p>}
      <div className="users-container">
        {users.map((user) => (
          <div key={user.id} className="user">
            {handleAvatar(user)}
            <div className="user-info">
              <p>{user.username}</p>
              <p>{user.email}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default SearchFriends;
