import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, ListGroup, Form, Button, Modal } from 'react-bootstrap';

export default function PartyFinder() {
  const [parties, setParties] = useState([]);
  const [search, setSearch] = useState("");
  const [raids, setRaids] = useState([]);
  const [selectedRaid, setSelectedRaid] = useState("");
  const [selectedRole, setSelectedRole] = useState("");
  const [selectedJob, setSelectedJob] = useState("");
  const [jobs, setJobs] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [description, setDescription] = useState("");

  useEffect(() => {
    // Fetch the raids and update the "raids" state
    axios.get('http://localhost:8080/raids/getRaidsTrue')
      .then(response => setRaids(response.data))
      .catch(error => console.log(error));
  }, []);

  useEffect(() => {
    if (selectedRole) {
      // Fetch the jobs based on the selected role
      axios.get(`http://localhost:8080/jobs/getJobsByRole/${selectedRole}`)
        .then(response => setJobs(response.data))
        .catch(error => console.log(error));
    }
  }, [selectedRole]);

  useEffect(() => {
    if (search) {
      // Perform the search based on the selected raid
      const selectedRaidId = parseInt(search);
      // Make an API call or perform the necessary logic to fetch parties using the selected raid
      // Update the "parties" state with the fetched parties
      const fetchedParties = []; // Replace with actual fetched parties
      setParties(fetchedParties);
    }
  }, [search]);

  const handleSearchChange = (event) => {
    const selectedRaidId = event.target.value;
    setSearch(selectedRaidId);
  };

  const handleRaidChange = (event) => {
    const selectedRaidId = event.target.value;
    setSelectedRaid(selectedRaidId);
  };

  const handleRoleChange = (event) => {
    setSelectedRole(event.target.value);
  };

  const handleJobChange = (event) => {
    setSelectedJob(event.target.value);
  };

  const handleDescriptionChange = (event) => {
    setDescription(event.target.value);
  };

  const handleOpenModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleCreateParty = () => {
    // Implement the logic to create a party using the selected raid, role, and job
    // You can make an API call to the backend to create a new party
    // After creating the party, you can update the "parties" state with the new party
    // Remember to close the modal after successfully creating the party
  };

  const isCreateButtonClickable = description.length >= 15 && selectedRaid !== "" && selectedRole !== "" && selectedJob !== "";

  return (
    <Container>
      <div className='py-4'>
        <h1>Party Finder</h1>
        <div className="d-flex flex-column align-items-center mb-3">
          <Form.Group style={{ width: '50%' }}>
            <Form.Label>Search by Raid:</Form.Label>
            <Form.Control as="select" value={search} onChange={handleSearchChange}>
              <option value="">Select an option</option>
              {raids.map(raid => (
                <option key={raid.id} value={raid.id}>{raid.name} --- level: ({raid.lvl})</option>
              ))}
            </Form.Control>
          </Form.Group>
          <Button variant="primary" onClick={handleOpenModal} style={{ borderRadius: "50%", width: '60px', height: '60px', opacity: 0.9, marginTop: "10px" }}>
            <img style={{ maxWidth: '100%', maxHeight: '100%', width: 'auto', height: 'auto' }} src='img/plus.png' alt="Create Party" />
          </Button>
        </div>
        <h3>Parties:</h3>
        <ListGroup>
          {parties.length > 0 ? (
            parties.map(party => (
              <ListGroup.Item key={party.id}>
                <h5>{party.name}</h5>
                <p>{party.description}</p>
                {/* Render other party details */}
              </ListGroup.Item>
            ))
          ) : (
            <p>No parties found.</p>
          )}
        </ListGroup>
      </div>
      <Modal show={showModal} onHide={handleCloseModal} dialogClassName="custom-modal" contentClassName="custom-modal-content">
        <Modal.Header closeButton>
          <Modal.Title>Create Party</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group>
            <Form.Label>Select Raid:</Form.Label>
            <Form.Control as="select" value={selectedRaid} onChange={handleRaidChange}>
              <option name="raid" value="">Select an option</option>
              {raids.map(raid => (
                <option key={raid.id} value={raid.id}>{raid.name} --- level: ({raid.lvl})</option>
              ))}
            </Form.Control>
          </Form.Group>
          {selectedRaid && (
            <Form.Group>
              <Form.Label>Select Role:</Form.Label>
              <div className="d-flex flex-wrap">
                {['DPS', 'TANK', 'HEALER'].map(role => (
                  <div key={role} className="d-flex flex-column align-items-center mx-3 mb-3">
                    <label>
                      <img
                        src={`/img/roles/${role.toLowerCase()}.png`}
                        alt={role}
                        style={{ width: '50px', height: '50px', marginBottom: '5px' }}
                      />
                      <input
                        type="radio"
                        name="role"
                        value={role}
                        checked={selectedRole === role}
                        onChange={handleRoleChange}
                        className="me-2"
                      />
                      {role}
                    </label>
                  </div>
                ))}
              </div>
            </Form.Group>
          )}
          {selectedRole && (
            <Form.Group>
              <Form.Label>Select Job:</Form.Label>
              <div className="d-flex flex-wrap">
                {jobs.map(job => (
                  <div key={job.id} className="d-flex flex-column align-items-center mx-3 mb-3">
                    <label>
                      <img
                        src={job.image}
                        alt={job.name}
                        style={{ width: '50px', height: '50px', marginBottom: '5px' }}
                      />
                      <input
                        type="radio"
                        name="job"
                        value={job.name}
                        checked={selectedJob === job.name}
                        onChange={handleJobChange}
                      />
                    </label>
                  </div>
                ))}
              </div>
            </Form.Group>
          )}
          {selectedRole && (
            <Form.Group>
              <Form.Label>Description:</Form.Label>
              <Form.Control
                type="text"
                value={description}
                onChange={handleDescriptionChange}
                placeholder="Enter description (minimum 15 characters)"
              />
            </Form.Group>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>Cancel</Button>
          <Button variant="primary" onClick={handleCreateParty} disabled={!isCreateButtonClickable}>Create</Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
}
