import React, { useState, useEffect } from 'react';
import axios from 'axios';

const SearchFriends = ({ currentUser }) => {
    const [searchQuery, setSearchQuery] = useState("");
    const [users, setUsers] = useState([]);

    const fetchUsers = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8080/usersList?search=${searchQuery}&current_user_username=${currentUser.username}`
        );
        setUsers(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    
    useEffect(() => {
      fetchUsers();
    }, [searchQuery, currentUser]);
    
    // Render the search bar and list of users
    return (
      <div>
        <input
          type="text"
          placeholder="Search users"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        {users.map((user) => (
          <div key={user.id}>
            <h3>{user.username}</h3>
            <p>{user.email}</p>
            <img src={user.avatarUrl} alt={`${user.username}'s avatar`} />
          </div>
        ))}
      </div>
    );
};

export default SearchFriends;
