import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Form, Button, Card, Container, Row, Col } from 'react-bootstrap';
import { Link } from 'react-router-dom';

export default function LogenHome() {
  if (!sessionStorage.getItem('username')) {
    window.location.href = '/';
  }
  const [pendingFriends, setPendingFriends] = useState([]);
  const [confirmedFriends, setConfirmedFriends] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');
  const [selectedFriend, setSelectedFriend] = useState(null);
  const [selectedFriendUsername, setSelectedFriendUsername] = useState(null);
  const [currentUser, setCurrentUser] = useState(sessionStorage.getItem('username'));
  const [messageInput, setMessageInput] = useState('');
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get(`http://localhost:8080/friend/pending?username=${currentUser}`);
        setPendingFriends(response.data);
        setErrorMessage('');
      } catch (error) {
        setPendingFriends([]);
        if (error.response) {
          const errorMessage = error.response.data.message;
          setErrorMessage(errorMessage);
        } else {
          setErrorMessage('An error occurred while fetching pending friends.');
        }
      }
    }
    fetchData();
  }, [currentUser]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get(`http://localhost:8080/friend/confirmed?username=${currentUser}`);
        setConfirmedFriends(response.data);
        setErrorMessage('');
      } catch (error) {
        setConfirmedFriends([]);
        if (error.response) {
          const errorMessage = error.response.data.message;
          setErrorMessage(errorMessage);
        } else {
          setErrorMessage('An error occurred while fetching confirmed friends.');
        }
      }
    }
    fetchData();
  }, [currentUser]);

  useEffect(() => {
    if (selectedFriend) {
      fetchMessages();
    }
  }, [selectedFriend]);

  useEffect(() => {
    const interval = setInterval(fetchMessages, 5000); // Fetch messages every 5 seconds

    return () => {
      clearInterval(interval); // Clean up the interval on component unmount
    };
  }, []);

  function fetchMessages() {
    if (selectedFriend) {
      const senderId = selectedFriend.sender.id;
      const receiverId = selectedFriend.receiver.id;

      axios
        .get(`http://localhost:8080/messages/messages?senderId=${senderId}&receiverId=${receiverId}`)
        .then(response => {
          const fetchedMessages = response.data;
          setMessages(fetchedMessages);
        })
        .catch(error => {
          console.error('Error fetching messages:', error);
        });
    }
  }

  function handleAddFriend(friendId) {
    axios
      .put(`http://localhost:8080/friend/${friendId}/accept`)
      .then(response => {
        window.location.href = '/';
        console.log('Friend request accepted successfully');
      })
      .catch(error => {
        alert('Error with the request, try it later');
        console.error('Error accepting friend request:', error);
      });
  }

  function handleCancelFriendship(friendId) {
    axios
      .delete(`http://localhost:8080/friend/${friendId}`)
      .then(response => {
        window.location.href = '/';
        console.log('Friend request cancelled successfully');
      })
      .catch(error => {
        alert('Error with the request, try it later');
        console.error('Error cancelling friend request:', error);
      });
  }

  function handleSelectFriend(friend) {
    setSelectedFriend(friend);
    if (friend.sender.username === currentUser) {
      setSelectedFriendUsername(friend.receiver.username);
    } else {
      setSelectedFriendUsername(friend.sender.username);
    }
  }

  function sendMessage() {
    let senderId;
    let receiverId;

    if (selectedFriend.sender.username === currentUser) {
      receiverId = selectedFriend.receiver.id;
      senderId = selectedFriend.sender.id;
    } else {
      receiverId = selectedFriend.sender.id;
      senderId = selectedFriend.receiver.id;
    }

    if (messageInput.trim() !== '') {
      const messageContent = messageInput;

      axios
        .post('http://localhost:8080/messages/addMessage', {
          senderId: senderId,
          receiverId: receiverId,
          content: messageContent
        })
        .then(response => {
          console.log('Message sent successfully');
          // Do something with the response if needed
        })
        .catch(error => {
          console.error('Error sending message:', error);
          alert('Error with the request, please try again later');
        });

      setMessageInput('');
    }
  }

  return (
    <Container fluid style={{ marginTop: '20px' }}>
      <Row>
        <Col xs={3} style={{ borderLeft: '#0d6efd 3px solid', borderRight: '#0d6efd 3px solid', overflow: 'hidden' }}>
          <h2>Friend List</h2>
          <div className="friend-list">
            {confirmedFriends.map(friend =>
              friend.pending === false && friend.sender.username === currentUser ? (
                <Card key={friend.id} className="friend-item" border="primary" onClick={() => handleSelectFriend(friend)}>
                  <a href="#" style={{ textDecoration: 'none' }}>
                    <div className="friend-info" style={{ display: 'flex' }}>
                      <div className="friend-avatar">
                        <img
                          src={
                            friend.receiver.avatarPath ||
                            `https://ui-avatars.com/api/?name=${friend.receiver.username}&background=007bff&color=fff`
                          }
                          style={{ width: '80px' }}
                          alt="Friend Avatar"
                          className="avatar"
                        />
                      </div>
                      <div className="friend-details" style={{ width: '100%' }}>
                        <p
                          className="username"
                          style={{
                            fontSize: '20px',
                            fontWeight: 'bold',
                            position: 'relative',
                            top: '15px',
                            height: '100%',
                            textAlign: 'center',
                            width: '100%'
                          }}
                        >
                          {friend.receiver.username}
                        </p>
                      </div>
                    </div>
                  </a>
                </Card>
              ) : friend.pending === false && friend.receiver.username === currentUser ? (
                <Card key={friend.id} className="friend-item" border="primary" onClick={() => handleSelectFriend(friend)}>
                  <a href="#" style={{ textDecoration: 'none' }}>
                    <div className="friend-info" style={{ display: 'flex' }}>
                      <div className="friend-avatar">
                        <img
                          src={
                            friend.sender.avatarPath ||
                            `https://ui-avatars.com/api/?name=${friend.sender.username}&background=007bff&color=fff`
                          }
                          style={{ width: '80px' }}
                          alt="Friend Avatar"
                          className="avatar"
                        />
                      </div>
                      <div className="friend-details" style={{ width: '100%' }}>
                        <p
                          className="username"
                          style={{
                            fontSize: '20px',
                            fontWeight: 'bold',
                            position: 'relative',
                            top: '15px',
                            height: '100%',
                            textAlign: 'center',
                            width: '100%'
                          }}
                        >
                          {friend.sender.username}
                        </p>
                      </div>
                    </div>
                  </a>
                </Card>
              ) : null
            )}
          </div>
        </Col>

        <Col xs={6} className="discord-col" style={{ height: '80vh' }}>
          {selectedFriend ? (
            <div className="chat-window">
              <div className="chat-header">
                <h2>Chat with {selectedFriendUsername}</h2>
              </div>
              <div className="conversation">
                {messages.map((message, index) => (
                  <div
                    key={index}
                    className={`message ${message.sender.username === currentUser ? 'sent' : 'received'}`}
                    style={{
                      display: 'flex',
                      justifyContent: message.sender.username === currentUser ? 'flex-end' : 'flex-start'
                    }}
                  >
                    <div
                      className="content"
                      style={{
                        backgroundColor: message.sender.username === currentUser ? '#2979ff' : '#e0e0e0',
                        color: message.sender.username === currentUser ? 'white' : 'black',
                        borderRadius: '8px',
                        padding: '8px'
                      }}
                    >
                      {message.content}
                    </div>
                  </div>
                ))}
              </div>
              <div className="input-area">
                <input
                  type="text"
                  placeholder="Type a message"
                  value={messageInput}
                  onChange={event => setMessageInput(event.target.value)}
                />
                <button onClick={sendMessage}>Send</button>
              </div>
            </div>
          ) : (
            <div className="no-friend-selected">
              <p>Select a friend to start a conversation.</p>
            </div>
          )}
        </Col>

        <Col xs={3} style={{ borderLeft: '#0d6efd 3px solid', borderRight: '#0d6efd 3px solid', overflow: 'hidden' }}>
          <h2>Friend Request</h2>
          <div className="friend-list">
            {pendingFriends.map(friend => (
              <Card key={friend.sender.id} className="friend-item" border="primary">
                <div className="friend-info" style={{ display: 'flex' }}>
                  <div className="friend-avatar">
                    <img
                      src={
                        friend.sender.avatarPath ||
                        `https://ui-avatars.com/api/?name=${friend.sender.username}&background=007bff&color=fff`
                      }
                      style={{ width: '80px' }}
                      alt="Friend Avatar"
                      className="avatar"
                    />
                  </div>
                  <p
                    className="username"
                    style={{
                      fontWeight: 'bold',
                      position: 'relative',
                      top: '15px',
                      height: '100%',
                      textAlign: 'center',
                      width: '100%'
                    }}
                  >
                    {friend.sender.username}
                  </p>
                  <div className="friend-details" style={{ width: '100%', display: 'flex', justifyContent: 'flex-end' }}>
                    <Button variant="primary" onClick={() => handleAddFriend(friend.id)}>
                      Accept petition
                    </Button>
                    <Button variant="danger" onClick={() => handleCancelFriendship(friend.id)}>
                      Cancel petition
                    </Button>
                  </div>
                </div>
              </Card>
            ))}
          </div>
        </Col>
      </Row>
      <Row>
        <Col xs={12} style={{ textAlign: 'center', marginTop: '20px' }}>
          <Link to="/">
            <Button variant="outline-primary">Logout</Button>
          </Link>
        </Col>
      </Row>
    </Container>
  );
}
