import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Form, Button, Card, Container, Row, Col } from 'react-bootstrap';
import { Link } from 'react-router-dom';

export default function LogenHome() {
  if (!sessionStorage.getItem('username')) {
    window.location.href = '/';
  }
  const [pendingFriends, setPendingFriends] = useState([]);
  const [confirmedFriends, setConfirmedFriends] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');
  const [selectedFriend, setSelectedFriend] = useState(null);
  const [selectedFriendUsername, setSelectedFriendUsername] = useState(null);
  const [currentUser, setCurrentUser] = useState(sessionStorage.getItem('username'));
  const [messageInput, setMessageInput] = useState('');
  const [messages, setMessages] = useState([]); // Added 'messages' state

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get(`http://localhost:8080/friend/pending?username=${currentUser}`);
        setPendingFriends(response.data);
        setErrorMessage('');
      } catch (error) {
        setPendingFriends([]);
        if (error.response) {
          const errorMessage = error.response.data.message;
          setErrorMessage(errorMessage);
        } else {
          setErrorMessage('An error occurred while fetching pending friends.');
        }
      }
    }
    fetchData();
  }, [currentUser]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get(`http://localhost:8080/friend/confirmed?username=${currentUser}`);
        setConfirmedFriends(response.data);
        setErrorMessage('');
      } catch (error) {
        setConfirmedFriends([]);
        if (error.response) {
          const errorMessage = error.response.data.message;
          setErrorMessage(errorMessage);
        } else {
          setErrorMessage('An error occurred while fetching confirmed friends.');
        }
      }
    }
    fetchData();
  }, [currentUser]);

  function handleAddFriend(friendId) {
    axios.put(`http://localhost:8080/friend/${friendId}/accept`)
      .then(response => {
        window.location.href = '/';
        console.log('Friend request accepted successfully');
      })
      .catch(error => {
        alert("Error with the request, try it later");
        console.error('Error accepting friend request:', error);
      });
  }

  function handleCancelFriendship(friendId) {
    axios.delete(`http://localhost:8080/friend/${friendId}`)
      .then(response => {
        window.location.href = '/';
        console.log('Friend request cancelled successfully');
      })
      .catch(error => {
        alert("Error with the request, try it later");
        console.error('Error cancelling friend request:', error);
      });
  }

  function handleSelectFriend(friend) {
    setSelectedFriend(friend); // Set the entire friend object as selectedFriend
    if (friend.sender.username === currentUser) {
      setSelectedFriendUsername(friend.receiver.username);
    } else {
      setSelectedFriendUsername(friend.sender.username);
    }

    const getMessages = (senderId, receiverId) => {
      return axios.get(`http://localhost:8080/messages/messages?senderId=${senderId}&receiverId=${receiverId}`);
    };

    const fetchMessages = () => {
      axios.all([
        getMessages(friend.sender.id, friend.receiver.id),
        getMessages(friend.receiver.id, friend.sender.id)
      ])
        .then(axios.spread((response1, response2) => {
          const messages1 = response1.data;
          const messages2 = response2.data;
          const allMessages = [...messages1, ...messages2];
          allMessages.sort((a, b) => a.timestamp.localeCompare(b.timestamp));
          setMessages(allMessages);
        }))
        .catch(error => {
          console.error('Error fetching messages:', error);
        });
    };

    fetchMessages();
  }

  function handleSendMessage(event) {
    event.preventDefault();

    if (messageInput.trim() === '') {
      return;
    }

    const message = {
      senderId: currentUser === selectedFriend.sender.username ? selectedFriend.sender.id : selectedFriend.receiver.id,
      receiverId: currentUser === selectedFriend.sender.username ? selectedFriend.receiver.id : selectedFriend.sender.id,
      content: messageInput.trim(),
    };

    axios.post('http://localhost:8080/messages/send', message)
      .then(response => {
        setMessages(prevMessages => [...prevMessages, response.data]);
        setMessageInput('');
      })
      .catch(error => {
        console.error('Error sending message:', error);
      });
  }

  return (
    <Container fluid style={{ marginTop: '20px' }}>
      <Row>
        <Col xs={6} className="discord-col">
          <Card className="friends-card">
            <Card.Header>Pending Friend Requests</Card.Header>
            {pendingFriends.length === 0 ? (
              <Card.Body className="no-friends-text">No pending friend requests.</Card.Body>
            ) : (
              <ul className="friends-list">
                {pendingFriends.map(friend => (
                  <li key={friend.id} className="friend">
                    <div className="friend-info">
                      <div className="friend-username">{friend.sender.username}</div>
                      <div className="friend-actions">
                        <Button
                          className="accept-button"
                          variant="success"
                          onClick={() => handleAddFriend(friend.sender.id)}
                        >
                          Accept
                        </Button>
                        <Button
                          className="cancel-button"
                          variant="danger"
                          onClick={() => handleCancelFriendship(friend.sender.id)}
                        >
                          Cancel
                        </Button>
                      </div>
                    </div>
                  </li>
                ))}
              </ul>
            )}
          </Card>

          <Card className="friends-card">
            <Card.Header>Confirmed Friends</Card.Header>
            {confirmedFriends.length === 0 ? (
              <Card.Body className="no-friends-text">No confirmed friends.</Card.Body>
            ) : (
              <ul className="friends-list">
                {confirmedFriends.map(friend => (
                  <li key={friend.id} className="friend">
                    <div
                      className={`friend-info ${selectedFriend === friend ? 'selected' : ''}`}
                      onClick={() => handleSelectFriend(friend)}
                    >
                      <div className="friend-username">{friend.sender.username}</div>
                    </div>
                  </li>
                ))}
              </ul>
            )}
          </Card>
        </Col>

        <Col xs={6} className="discord-col" style={{ height: "80vh" }}>
          {selectedFriend ? (
            <div className="chat-window">
              <div className="chat-header">
                <h2>Chat with {selectedFriendUsername}</h2>
              </div>
              <div className="conversation">
                {messages.map((message, index) => (
                  <div
                    key={index}
                    className={`message ${message.senderId === currentUser ? 'sent' : 'received'}`}
                    style={{
                      display: 'flex',
                      justifyContent: message.senderId === currentUser ? 'flex-end' : 'flex-start',
                    }}
                  >
                    <div
                      className="content"
                      style={{
                        backgroundColor: message.senderId === currentUser ? 'blue' : 'white',
                        color: message.senderId === currentUser ? 'white' : 'black',
                      }}
                    >
                      {message.content}
                    </div>
                  </div>
                ))}
              </div>
              <div className="input-area">
                <Form onSubmit={handleSendMessage}>
                  <Form.Group>
                    <Form.Control
                      type="text"
                      value={messageInput}
                      onChange={event => setMessageInput(event.target.value)}
                      placeholder="Type a message..."
                    />
                  </Form.Group>
                  <Button variant="primary" type="submit">
                    Send
                  </Button>
                </Form>
              </div>
            </div>
          ) : (
            <div className="select-friend">
              <h2>Select a friend to start chatting</h2>
            </div>
          )}
        </Col>
      </Row>

      <div className="logout-link">
        <Link to="/" onClick={() => sessionStorage.clear()}>
          Logout
        </Link>
      </div>
    </Container>
  );
}
