import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Row, Col } from 'react-bootstrap';
import { Pagination } from 'react-bootstrap';
import ReactPaginate from 'react-paginate';

function SearchFriends() {
  const [searchQuery, setSearchQuery] = useState('');
  const [users, setUsers] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');
  const [currentPage, setCurrentPage] = useState(0);
  const [perPage, setPerPage] = useState(10);
  const currentUser = sessionStorage.getItem('username');

  const handleSearch = async () => {
    try {
        const response = await axios.get(`http://localhost:8080/usersList/search`, {
            params: {
                query: searchQuery,
                currentUser: currentUser,
                page: currentPage,
                size: perPage
            }
        });
        setUsers(response.data.content);
        setTotalPages(response.data.totalPages);
        setErrorMessage('');
    } catch (error) {
        setUsers([]);
        if (error.response) {
            const errorMessage = error.response.data.message;
            setErrorMessage(errorMessage);
        } else {
            setErrorMessage('An error occurred while searching for users.');
        }
    }
};

  
  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get(`http://localhost:8080/users/usersList?currentUser=${currentUser}`);
        setUsers(response.data);
        setErrorMessage('');
      } catch (error) {
        setUsers([]);
        if (error.response) {
          const errorMessage = error.response.data.message;
          setErrorMessage(errorMessage);
        } else {
          setErrorMessage('An error occurred while fetching users.');
        }
      }
    }
    fetchData();
  }, [currentUser]);

  return (
    <div>
      <h1>Search for Friends</h1>
      <div>
        <label htmlFor="search-input">Search: </label>
        <input id="search-input" type="text" value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} />
        <button onClick={handleSearch}>Search</button>
      </div>
      {errorMessage && <p>{errorMessage}</p>}
      <div>
        <Row>
          {users.map((user) => (
            <Col sm={4} key={user.id}>
              <div className="border p-3">
                <img src={user.avatarUrl} alt={`${user.username}'s avatar`} className="img-fluid" />
                <p className="mt-3 mb-0"><strong>{user.username}</strong></p>
                <p className="text-muted">{user.email}</p>
              </div>
            </Col>
          ))}
        </Row>
      </div>
    </div>
  );
}

export default SearchFriends;
