import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { Modal, Button, Card, Container, Row, Col } from 'react-bootstrap';
import { Link, useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

export default function Party() {
    
  let navigate = useNavigate();
  const { partyId } = useParams();
  const [parties, setParties] = useState([]);
  
  if (sessionStorage.getItem("username") === "admin") {
    navigate("/admin");
    //window.location.href = '/admin';
  } else if (!sessionStorage.getItem("username")) {
    navigate("/");
    //window.location.href = '/';
  }
  useEffect(() => {
    async function fetchParties() {
      try {
        const response = await axios.post(`http://localhost:8080/parties/partiesForMember/${sessionStorage.getItem("username")}`);
        setParties(response.data);
      } catch (error) {
        console.error('Error fetching parties:', error);
      }
    }

    fetchParties();
  }, []);

  ////////////return start here////////////////
  return (
    <Container fluid style={{ marginTop: '20px' }}>
      <Row>
        <Col xs={3} className='friendship'>
          <h2>Party</h2>
          <div className='Party'>
            {parties.map((member) => (
              <div key={member.id}>
                <h4>{member.name}</h4>
                <p>{member.description}</p>
              </div>
            ))}
          </div>
        </Col>

        <Col xs={8} className="discord-col" style={{ height: "80vh" }}>
          <div className="chat-window">
            <div className="chat-header">
              <h2>Chat with</h2>
            </div>
            <div className="conversation" id="scroll"></div>
            <div className="input-area">
              <input type="text" placeholder="Type a message" />
              {/*Send message */}
              <button>Send</button>
              {/* Button to open the emote modal */}
              <button>Emotes</button>
            </div>
          </div>
        </Col>
      </Row>
    </Container>
  );
}
