import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { Modal, Button, Card, Container, Row, Col } from 'react-bootstrap';
import { Link, useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

export default function Party() {
  let navigate = useNavigate();
  const { partyId } = useParams();
  const [party, setParty] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [currentUser, setCurrentUser] = useState(sessionStorage.getItem('username'));
  const [messageInput, setMessageInput] = useState('');
  const [selectedEmote, setSelectedEmote] = useState(null);
  const [emotes, setEmotes] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [partyMessages, setPartyMessages] = useState([]);

  if (sessionStorage.getItem("username") === "admin") {
    navigate(`/admin`);
  } else if (!sessionStorage.getItem("username")) {
    navigate(`/`);
  } else {
    if (!sessionStorage.getItem("party")) {
      navigate(`/partyFinder`);
    }
  }

  useEffect(() => {
    async function fetchEmotes() {
      try {
        const response = await axios.get('http://localhost:8080/emotes');
        setEmotes(response.data);
      } catch (error) {
        console.error('Error fetching emotes:', error);
      }
    }

    fetchEmotes();
  }, []);

  function handleEmoteSelection(emote) {
    setSelectedEmote(emote);
    setShowModal(false);

    const sendEmote = (emoteId) => {
      return axios.post('http://localhost:8080/parties/sendEmote', {
        partyId: partyId,
        currentUser: currentUser,
        message: messageInput,
        emoteId: emoteId
      });
    };

    sendEmote(emote.id)
      .then(response => {
        console.log('Emote sent successfully');
        // Do something with the response if needed
      })
      .catch(error => {
        console.error('Error sending emote:', error);
        alert('Error sending emote. Please try again later.');
      });
  }

  useEffect(() => {
    async function fetchParty() {
      try {
        const response = await axios.get(`http://localhost:8080/parties/partyById/${partyId}`);
        setParty(response.data);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching party:', error);
      }
    }

    fetchParty();
  }, [partyId]);

  useEffect(() => {
    async function fetchPartyMessages() {
      try {
        const response = await axios.get(`http://localhost:8080/parties/partyMessages/${partyId}`);
        setPartyMessages(response.data);
      } catch (error) {
        console.error('Error fetching party messages:', error);
      }
    }

    fetchPartyMessages();
  }, [partyId]);

  const handleLeaveParty = async () => {
    try {
      const currentUser = sessionStorage.getItem("username");
      const response = await axios.post(`http://localhost:8080/parties/leaveParty`, {
        partyId: partyId,
        currentUser: currentUser
      });
      sessionStorage.removeItem('party');
      navigate(`/partyFinder`);
    } catch (error) {
      alert("error leaving the party");
      console.error('Error leaving the party:', error);
    }
  };

  const sendMessage = async () => {
    try {
      const response = await axios.post(`http://localhost:8080/parties/sendMessage`, {
        partyId: partyId,
        currentUser: currentUser,
        message: messageInput
      });
      // Clear the message input after sending
      setMessageInput('');
    } catch (error) {
      console.error('Error sending message:', error);
      // Handle error, display an alert, etc.
    }
  };

  if (isLoading) {
    return <div>Loading...</div>;
  }

  return (
    <Container fluid style={{ marginTop: '20px' }}>
      <Row>
        <Col xs={3} className='friendship'>
          <h2>Party</h2>
          <div className='Party'>
            {(() => {
              let memberElements = [];
              for (let i = 1; i < 9; i++) {
                let image = "";
                if (i === 1 || i === 2) {
                  image = "/img/roles/tank.png";
                } else if (i === 3 || i === 4) {
                  image = "/img/roles/healer.png";
                } else {
                  image = "/img/roles/dps.png";
                }
                let member = party[`member${i}`];
                if (member == null) {
                  memberElements.push(
                    <div key={i} className="friendCard">
                      <div className="friend-info" style={{ display: 'flex', height: 'max-content' }}>
                        <div className="friend-avatar">
                          <img
                            src={image}
                            style={{ width: "80px", height: "max-content" }}
                            alt="Friend Avatar"
                            className="avatar"
                          />
                        </div>
                        <div className="friend-details" style={{ width: "100%", lineHeight: "40px" }}>
                          <p className="username" style={{ fontSize: "14px", fontWeight: "bold", marginLeft: "10px" }}>{"Awaiting Party Member"}</p>
                        </div>
                      </div>
                    </div>
                  );
                } else {
                  memberElements.push(
                    <div key={i} className="friendCard">
                      <div className="friend-info" style={{ display: 'flex', height: 'max-content' }}>
                        <div className="friend-avatar">
                          <img
                            src={"/" + member.job.image}
                            style={{ width: "80px", height: "max-content" }}
                            alt="Friend Avatar"
                            className="avatar"
                          />
                        </div>
                        <div className="friend-details" style={{ width: "100%", lineHeight: "40px" }}>
                          <p className="username" style={{ fontSize: "14px", fontWeight: "bold", marginLeft: "10px" }}>{member.user.nickname}</p>
                        </div>
                        <div className="friend-avatar">
                          <img
                            src={"/" + member.user.avatarPath ||
                              `https://ui-avatars.com/api/?name=${member.user.nickname}&background=007bff&color=fff`}
                            style={{ width: "80px", height: "max-content", borderRadius: "50%" }}
                            alt="Friend Avatar"
                            className="avatar"
                          />
                        </div>
                      </div>
                    </div>
                  );
                }
              }
              return memberElements;
            })()}
          </div>
        </Col>
        <Col xs={8} className="discord-col" style={{ height: "80vh" }}>
          <div className="chat-window">
            <div className="chat-header">
              <h2>{party.raidName}</h2>
              <Link to="/partyFinder">
                <Button variant="light" onClick={handleLeaveParty}>
                  Leave Party
                </Button>
              </Link>
            </div>
            <div className="chat-messages">
              {partyMessages.map(message => (
                <div
                  key={message.id}
                  className={`chat-message ${message.memberNickname === currentUser ? 'right' : 'left'}`}
                >
                  <div className="message-header">
                    <div className="message-sender">
                      <img
                        src={"/" + message}
                        alt="Member Avatar"
                        className="avatar"
                      />
                      <p className="username">{message.memberNickname}</p>
                    </div>
                    <p className="message-timestamp">{message.timestamp}</p>
                  </div>
                  <div className="message-content">
                    {message.emote && (
                      <img
                        src={message.emote.emotePath}
                        alt={message.emote.emoteName}
                        className="emote"
                      />
                    )}
                    <p className="message-text">{message.content}</p>
                  </div>
                </div>
              ))}
            </div>
            <div className="message-input">
              <input
                type="text"
                placeholder="Type a message..."
                value={messageInput}
                onChange={e => setMessageInput(e.target.value)}
              />
              <div className="emote-selector">
                <Button variant="light" onClick={() => setShowModal(true)}>
                  Emotes
                </Button>
              </div>
              <Button variant="primary" onClick={sendMessage}>
                Send
              </Button>
            </div>
          </div>
        </Col>
      </Row>
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Select Emote</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="emote-list">
            {emotes.map(emote => (
              <div
                key={emote.id}
                className={`emote-item ${selectedEmote && selectedEmote.id === emote.id ? 'selected' : ''}`}
                onClick={() => handleEmoteSelection(emote)}
              >
                <img src={emote.emotePath} alt={emote.emoteName} className="emote-image" />
              </div>
            ))}
          </div>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
}
