import { useState, useEffect } from "react";
import axios from "axios";

function SearchFriends() {
  const [searchText, setSearchText] = useState("");
  const [users, setUsers] = useState([]);
  const [errorMessage, setErrorMessage] = useState("");

  useEffect(() => {
    axios
      .get("http://localhost:8080/usersList")
      .then((response) => {
        setUsers(response.data.filter((user) => user.username !== sessionStorage.getItem('username')));
      })
      .catch((error) => {
        setErrorMessage(error.message);
      });
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    axios
      .get(`http://localhost:8080/users/search?searchText=${searchText}`)
      .then((response) => {
        setUsers(response.data.filter((user) => user.username !== sessionStorage.getItem('username')));
      })
      .catch((error) => {
        setErrorMessage(error.message);
      });
  };

  return (
    <div>
      <form onSubmit={handleSearch}>
        <label htmlFor="search">Search:</label>
        <input
          type="text"
          id="search"
          value={searchText}
          onChange={(e) => setSearchText(e.target.value)}
        />
        <button type="submit">Search</button>
      </form>
      {errorMessage && <p>{errorMessage}</p>}
      <div>
        {users.map((user) => (
          <div key={user.id}>
            <h3>{user.username}</h3>
            <p>{user.email}</p>
            {user.avatarPath && <img src={`http://localhost:8080/${user.avatarPath}`} alt="Avatar" />}
          </div>
        ))}
      </div>
    </div>
  );
}

export default SearchFriends;
